import React, { useEffect, useState } from 'react';
import TopList from './Top10';
import { Row, Col } from 'react-bootstrap';
import Search from './Search';

function Player() {
    const [ topArtistas, setTopArtistas ] = useState({});

    useEffect(() => {
        fetch('http://ws.audioscrobbler.com/2.0/?method=chart.gettopartists&api_key=cfedf7f80f5776d69845ed9dd0b397e3&format=json&page=1&limit=10')
            .then(response => {
                return response.json();
            })
            .then(data => {
                setTopArtistas(data.artists);
            })
    }, []);

    function handleChangeFoundArtistas(values){
        console.log(values);
        setTopArtistas(values);
    }
    

    return(
        <Row>
            <Col md={4}>
                <div className="list">
                    <h1>Top 10 Artistas</h1>
                    {
                        topArtistas.length > 0 &&
                        <TopList artistas={topArtistas.artists} />
                    }
                </div>
            </Col>
            <Col md={4}>
                <Search foundArtistas={(values) => handleChangeFoundArtistas(values)}/>
            </Col>
        </Row>
    );
}

export default Player;
