import React, { useState } from 'react';
import { Tabs, Tab, Form, Button } from 'react-bootstrap';
import { propTypes } from 'react-bootstrap/esm/Image';

function Search(props) {
    const [ album, setAlbum ] = useState({});
    const [ artista, setArtista ] = useState({});
    const [ foundArtista, setFoundArtista ] = useState({});
    const [ foundAlbum, setFoundAlbum ] = useState({});

    async function handleSearchArtista(){
        const found = await fetch(`http://ws.audioscrobbler.com/2.0/?method=artist.search&artist=${artista}&api_key=cfedf7f80f5776d69845ed9dd0b397e3&format=json`)
                                .then(response => {
                                    if (response.ok)
                                        return response.json();
                                });
        setFoundArtista(found.results.artistmatches);
        props.foundArtistas(found.results.artistmatches);             
    }

    function handleChangeArtista(event){
        setArtista(event.target.value);
    }

    return (
        <Tabs defaultActiveKey="artista" id="uncontrolled-tab-example">
            <Tab eventKey="artista" title="Artista">
                <Form >
                    <Form.Group>
                        <Form.Label>Buscar por artista</Form.Label>
                        <Form.Control onChange={handleChangeArtista} placeholder="Digite o nome do artista" />
                    </Form.Group>
                    <Button onClick={handleSearchArtista}>Buscar</Button>
                </Form>
            </Tab>
            <Tab eventKey="album" title="Album">
                <Form>
                    <Form.Group>
                        <Form.Label>Buscar por album</Form.Label>
                        <Form.Control placeholder="Digite o nome do album" />
                    </Form.Group>
                    <Button type="submit">Buscar</Button>
                </Form> 
            </Tab>
        </Tabs>
    );
}

export default Search;
